import styled from 'styled-components/native';

export const FormInput = styled.TextInput`
    height: 15px;
    border-color: gray;
    border-width: 0;
    margin-bottom: 1px;
    /* padding-bottom: 15px; */
    border-bottom-width: 1px;
    :active {
        border: none;
    };
    padding
      
`
